interface IBook {
    function addBook(bytes32, address) external;
    function exists(bytes32) external returns (bool status);
    function deleteBook(bytes32) external;
    function getAuthorBooks(address) external view returns (bytes32[10] memory);
    function getAuthor(bytes32 _id) external view returns (address bookAuthor);
    function numAuthorBooks(address) external returns (uint num);
}","keccak256":"0x574f7c21ffa8a519b50d4f57ca328334eb7f82bea4b90380086aa0bf0d7c7ccb","license":"UNLICENSED"},"contracts/interfaces/IPay.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

interface IPay {
    function createPool(bytes32 _id, uint _price, address _author) external;
    function deletePool(bytes32 _id, address _author) external;
    function addContribution(bytes32 _id, address _contributor, uint _amt) external;
    function withdrawContribution(bytes32 _id, address _contributor) external returns (uint amt);
    function redeemBook(bytes32 _id, uint _phase, address _contributor) external returns (bool status);
    function currentPhase(bytes32 _id) external view returns (uint);
    function getAuthorWithdrawn(bytes32 _id, uint _phase, address _author) external view returns (bool);
    function setAuthorWithdrawn(bytes32 _id, uint _phase, address _author) external;
    function getAuthorFee(bytes32 _id) external view returns (uint);
}","keccak256":"0x1e51d10b7d6e270f7f537600c203acb571d5ef6ff8ff8ec076f2ab4bd6f3a4be","license":"UNLICENSED"},"contracts/libraries/SafeMath.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

library SafeMath{
    function add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x, 'ds-math-add-overflow');
    }

    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x, 'ds-math-sub-underflow');
    }

    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x, 'ds-math-mul-overflow');
    }

    function div(uint a, uint b) internal pure returns (uint) {
        unchecked {
            if (b == 0) return (0);
            return (a / b);
        }
    }
}","keccak256":"0x9b4cbf1f5e3010dcee1d1d4020cf3dd631bd710e6e4d76e6381722d19409d820","license":"UNLICENSED"},"hardhat/console.sol":{"content":"// SPDX-License-Identifier: MIT
